INSERT INTO `shows` (`id`, `slug`, `title`, `description`, `poster_url`, `bookable`, `price`, `created_at`, `updated_at`, `location_id`) VALUES
(1, 'ayiti', 'Ayiti', 'Un homme est bloqué à l’aéroport.\n Questionné par les douaniers, il doit alors justifier son identité, et surtout prouver qu\'il est haïtien – qu\'est-ce qu\'être haïtien ?', 'ayiti.jpg', 1, '9.00', '2020-04-21 19:08:44.583000', NULL, 1),
(2, 'cible-mouvante', 'Cible mouvante', 'Dans ce « thriller d’anticipation », des adultes semblent alimenter et véhiculer une crainte féroce envers les enfants âgés entre 10 et 12 ans.', 'cible.jpg', 1, '9.00', '2020-04-21 19:08:53.156000', NULL, 2),
(3, 'ceci-nest-pas-un-chanteur-belge', 'Ceci n\'est pas un chanteur belge', 'Non peut-être ?!\nEntre Magritte (pour le surréalisme comique) et Maigret (pour le réalisme mélancolique), ce dixième opus semalien propose quatorze nouvelles chansons mêlées à de petits textes humoristiques et à quelques fortes images poétiques.', 'claudebelgesaison220.jpg', 1, '5.50', '2020-04-21 19:08:53.189000', '2020-04-21 19:08:53.189000', NULL),
(4, 'manneke', 'Manneke… !', 'A tour de rôle, Pierre se joue de ses oncles, tantes, grands-parents et surtout de sa mère.', 'wayburn.jpg', 1, '10.50', '2020-04-21 19:09:02.426000', '2020-04-21 19:09:02.426000', 3),
(5, 'le-bourgeois-gentilhomme', 'Le Bourgeois gentilhomme', 'Comédie-ballet en cinq actes, cette pièce de Molière, avec musique de Lully, fut créée en 1670. Elle est aujourd’hui considérée comme un chef-d’œuvre du théâtre comique.', 'spectacle.jpg', 1, '12.00', '2022-05-06 15:30:00.000000', NULL, 1),
(6, 'le-misanthrope', 'Le Misanthrope', 'Comédie de Molière en cinq actes et en vers, créée en 1666, cette pièce est l’un des grands chefs-d’œuvre du théâtre français.', 'spectacle.jpg', 1, '15.00', '2022-06-15 19:30:00.000000', NULL, 1),
(7, 'les-fourberies-de-scapin', 'Les Fourberies de Scapin', 'Comédie en trois actes de Molière créée en 1671. La pièce est aujourd’hui considérée comme l’une des plus grandes comédies de Molière.', 'spectacle.jpg', 1, '10.00', '2022-07-20 20:00:00.000000', NULL, 1),
(8, 'le-malade-imaginaire', 'Le Malade imaginaire', 'Comédie-ballet en trois actes de Molière avec une musique de Marc-Antoine Charpentier. La pièce a été créée en 1673.', 'lemaladeimaginaire.jpg', 1, '12.50', '2022-08-10 15:00:00.000000', NULL, 1),
(9, 'antigone', 'Antigone', 'Antigone est une tragédie grecque de Sophocle, créée en 442 av. J.-C. Elle met en scène une héroïne éponyme qui brave l’interdiction de son oncle le roi Créon de donner une sépulture à son frère Polynice.', 'antigone.jpg', 1, '18.00', '2022-09-01 18:30:00.000000', NULL, 2),
(10, 'oedipe-roi', 'Oedipe Roi', 'Œdipe roi est une tragédie grecque de Sophocle écrite vers 429 av. J.-C. Elle raconte l’histoire du roi Œdipe qui, cherchant à fuir son destin, découvre finalement qu’il a tué son père et épousé sa mère.', 'oediperoi.jpg', 1, '20.00', '2022-09-15 19:00:00.000000', NULL, 2),
(11, 'le-petit-monde-de-don-camillo', 'Le Petit Monde de Don Camillo', 'Adaptation d’une série de romans humoristiques italiens créés par Giovannino Guareschi, cette pièce est un hymne à l’amitié et à la tolérance.', 'doncamillo.jpg', 1, '10.00', '2022-10-07 20:00:00.000000', NULL, 1),
(12, 'les-femmes-savantes', 'Les Femmes savantes', 'Comédie en cinq actes de Molière, créée en 1672, cette pièce est une satire de l’affectation précieuse et de la pédanterie savante.', 'lesfemmessavantes.jpg', 1, '12.00', '2022-11-10 20:00:00.000000', NULL, 1),
(13, 'le-lac-des-cygnes', 'Le Lac des cygnes', 'Ballet en quatre actes de Piotr Ilitch Tchaïkovski, créé en 1877. Cette œuvre est l’un des joyaux du ballet classique.', 'lelacdescygnes.jpg', 1, '25.00', '2022-12-20 20:00:00.000000', NULL, 1),
(14, 'la-belle-au-bois-dormant', 'La Belle au bois dormant', 'Ballet en trois actes de Piotr Ilitch Tchaïkovski, créé en 1890. Cette œuvre est inspirée du conte de Charles Perrault.', 'labelleauboisdormant.jpg', 1, '25.00', '2023-01-15 15:00:00.000000', NULL, 1),
(15, 'la-servante-ecarlate', 'La Servante écarlate', 'Adaptation du roman dystopique de Margaret Atwood, cette pièce se déroule dans un monde où la fertilité des femmes est devenue très rare et où certaines sont forcées de devenir des « servantes » pour procréer.', 'laservanteecarlate.jpg', 1, '15.00', '2023-02-23 20:00:00.000000', NULL, 3),
(16, 'la-guerre-des-sexes', 'La Guerre des sexes', 'Dans cette pièce, deux couples se disputent sur les différences entre hommes et femmes, et finissent par échanger leur rôle respectif.', 'laguerredessexes.jpg', 1, '10.00', '2023-03-15 20:00:00.000000', NULL, 2),
(17, 'le-roi-lion', 'Le Roi Lion', 'Le Roi Lion est une comédie musicale basée sur le film danimation éponyme de Disney. Elle raconte lhistoire de Simba, un lionceau qui doit apprendre à devenir roi de la Terre des Lions après la mort de son père, Mufasa.', 'le-roi-lion.jpg', 1, '49.99', '2023-05-08 12:00:00.000000', NULL, 1),
(18, 'les-miserables', 'Les Misérables', 'Les Misérables est une comédie musicale française basée sur le roman éponyme de Victor Hugo. Elle raconte lhistoire de Jean Valjean, un ancien bagnard qui cherche à se racheter aux yeux de la société.', 'les-miserables.jpg', 1, '59.99', '2023-05-09 10:00:00.000000', NULL, 2),
(19, 'notre-dame-de-paris', 'Notre-Dame de Paris', 'Notre-Dame de Paris est une comédie musicale française basée sur le roman éponyme de Victor Hugo. Elle raconte lhistoire de Quasimodo, un sonneur de cloches bossu qui tombe amoureux de la belle Esmeralda.', 'notre-dame-de-paris.jpg', 1, '69.99', '2023-05-10 15:00:00.000000', NULL, 1),
(20, 'romeo-et-juliette', 'Roméo et Juliette', 'Roméo et Juliette est une comédie musicale française basée sur la célèbre pièce de théâtre de William Shakespeare. Elle raconte lhistoire damour tragique entre les deux jeunes amants de familles rivales.', 'romeo-et-juliette.jpg', 1, '39.99', '2023-05-11 20:00:00.000000', NULL, 3),
(21, 'le-fantome-de-lopera', 'Le Fantôme de lOpéra', 'Le Fantôme de lOpéra est une comédie musicale française basée sur le roman éponyme de Gaston Leroux. Elle raconte lhistoire dun mystérieux fantôme qui hante lOpéra Garnier et qui tombe amoureux de la chanteuse Christine.', 'le-fantome-de-lopera.jpg', 1, '49.99', '2023-05-12 18:00:00.000000', NULL, 2),
(22, 'le-reveil-de-la-force', 'Star Wars : Le Réveil de la Force', 'Star Wars : Le Réveil de la Force est une adaptation de lépisode VII de la saga Star Wars. Le spectacle présente les aventures de Rey, Finn et Poe Dameron alors quils cherchent à retrouver Luke Skywalker et à vaincre le Premier Ordre.', 'star-wars-le-reveil-de-la-force.jpg', 1, '89.99', '2023-05-15 19:30:00.000000', NULL, 3),
(23, 'le-roi-arthur', 'Le Roi Arthur', 'Le Roi Arthur est une comédie musicale française qui raconte lhistoire de la légende arthurienne. La pièce suit la vie dArthur, de son épée Excalibur et de ses chevaliers de la Table Ronde dans leur quête pour unir le royaume de Camelot.', 'le-roi-arthur.jpg', 1, '49.99', '2023-05-16 20:00:00.000000', NULL, 1),
(24, 'la-famille-addams', 'La Famille Addams', 'La Famille Addams est une comédie musicale basée sur les personnages de la célèbre série télévisée américaine. La pièce suit les aventures de la famille Addams alors quils accueillent un nouveau membre dans leur famille, mais qui cache un secret.', 'la-famille-addams.jpg', 1, '69.99', '2023-05-17 15:00:00.000000', NULL, 2),
(25, 'notre-dame-de-paris-2', 'Notre-Dame de Paris', 'Notre-Dame de Paris est une comédie musicale française basée sur le roman éponyme de Victor Hugo. Elle raconte lhistoire de Quasimodo, un sonneur de cloches bossu qui tombe amoureux de la belle Esmeralda.', 'notre-dame-de-paris.jpg', 1, '69.99', '2023-05-18 19:00:00.000000', NULL, 1),
(26, 'les-10-commandements', 'Les 10 Commandements', 'Les 10 Commandements est une comédie musicale française qui raconte lhistoire de Moïse, le prophète biblique. La pièce suit sa vie depuis son enfance jusqu à son leadership en tant que libérateur du peuple hébreu et son ascension pour recevoir les 10 commandements.', 'les-10-commandements.jpg', 1, '59.99', '2023-05-19 20:00:00.000000', NULL, 3),
(27, 'le-fantome-de-l-opera', 'Le Fantôme de lOpéra', 'Le Fantôme de lOpéra est une comédie musicale française basée sur le roman éponyme de Gaston Leroux. Elle raconte lhistoire dun mystérieux fantôme qui hante lOpéra Garnier à Paris et tombe amoureux de la chanteuse Christine.', 'le-fantome-de-l-opera.jpg', 1, '79.99', '2023-05-21 15:00:00.000000', NULL, 2),
(28, 'la-belle-et-la-bete', 'La Belle et la Bête', 'La Belle et la Bête est une comédie musicale française basée sur le conte de fées éponyme. Elle raconte lhistoire de Belle, une jeune femme qui tombe amoureuse de la Bête, un prince maudit transformé en monstre.', 'la-belle-et-la-bete.jpg', 1, '69.99', '2023-05-22 19:00:00.000000', NULL, 3),
(29, 'mozart-l-opera-rock', 'Mozart lOpéra Rock', 'Mozart lOpéra Rock est une comédie musicale française basée sur la vie de Wolfgang Amadeus Mozart. Elle raconte lhistoire de sa vie et de son succès en tant que compositeur, tout en explorant ses relations avec sa famille et ses amis.', 'mozart-l-opera-rock.jpg', 1, '49.99', '2023-05-23 20:00:00.000000', NULL, 1),
(30, 'le-monde-renverse', 'Le Monde renversé', 'L\'équipe du Monde renversé propose une enquête théâtrale inédite dans les coulisses du pouvoir où se mêlent mensonges, manipulations et jeux de dupes.', 'lemonderenverse.jpg', 1, '12.50', '2023-05-15 19:30:00.000000', NULL, 1),
(31, 'antigone-auschwitz', 'Antigone à Auschwitz', 'L’histoire d’Antigone revisitée dans le contexte de la Shoah. Un spectacle poignant qui questionne la place de l’homme dans l’histoire et face à la barbarie.', 'antigoneauschwitz.jpg', 1, '15.00', '2023-06-05 20:00:00.000000', NULL, 2),
(32, 'voyage-dans-les-memoires-d-un-fou', 'Voyage dans les mémoires d\'un fou', 'Inspiré des écrits de Gogol, le spectacle plonge le spectateur dans l’univers d’un personnage tourmenté, pris dans un délire mystique et hallucinatoire.', 'voyagememoiresfou.jpg', 1, '8.00', '2023-06-10 21:00:00.000000', NULL, 3),
(33, 'les-justes', 'Les Justes', 'La pièce de Camus raconte l’histoire d’un groupe de terroristes socialistes russes qui prépare un attentat contre le grand-duc Serge, oncle du tsar. Une réflexion sur le terrorisme et l’engagement politique.', 'lesjustes.jpg', 1, '12.00', '2023-07-01 20:00:00.000000', NULL, 4),
(34, 'les-precieuses-ridicules', 'Les Précieuses ridicules', 'Comédie de Molière qui met en scène deux jeunes femmes prétentieuses et vaniteuses qui se moquent des prétendants que leur père leur présente. Une satire de la préciosité.', 'lesprecieuses.jpg', 1, '9.50', '2023-07-15 20:00:00.000000', NULL, 5),
(35, 'le-petit-monde-de-georges-brassens', 'Le petit monde de Georges Brassens', 'Un spectacle musical qui rend hommage à l’univers poétique et malicieux de Georges Brassens, à travers ses chansons les plus connues mais aussi des titres plus rares.', 'brassens.jpg', 1, '14.00', '2023-08-05 21:00:00.000000', NULL, 3),
(36, 'le-cid', 'Le Cid', 'Tragédie en cinq actes de Pierre Corneille, créée en 1637, Le Cid est une pièce emblématique du théâtre français du XVIIème siècle.', 'lecid.jpg', 1, '8.00', '2023-06-15 20:00:00.000000', NULL, 2),
(37, 'les-troyennes', 'Les Troyennes', 'Tragédie d’Euripide qui a été créée en 415 av. J.-C. La pièce se déroule juste après la prise de Troie par les Grecs et met en scène les femmes troyennes.', 'lestroyennes.jpg', 1, '10.50', '2023-07-18 19:30:00.000000', NULL, 2),
(38, 'le-malade-imaginaire-2', 'Le Malade imaginaire', 'Comédie-ballet en trois actes de Molière avec une musique de Marc-Antoine Charpentier. La pièce a été créée en 1673.', 'lemaladeimaginaire.jpg', 1, '12.50', '2023-08-10 15:00:00.000000', NULL, 2),
(39, 'le-tartuffe', 'Le Tartuffe', 'Comédie en cinq actes de Molière créée en 1664, cette pièce a été l\'objet d\'une violente querelle entre les dévots et les gens de théâtre de l\'époque. Aujourd\'hui elle est considérée comme une des grandes pièces du répertoire classique.', 'letartuffe.jpg', 1, '14.00', '2022-09-01 19:00:00.000000', NULL, 1),
(40, 'les-aventuriers-de-l-espace', 'Les Aventuriers de l\'Espace', 'Une équipe d\'astronautes part en mission pour sauver la planète X de l\'extinction', 'les-aventuriers-de-l-espace-poster.jpg', 1, '25.99', '2023-05-08 15:30:00.000000', NULL, 1),
(41, 'le-mystere-du-chateau-hante', 'Le Mystère du Château Hanté', 'Une détective enquête sur une série de phénomènes étranges qui se produisent dans un château abandonné', 'le-mystere-du-chateau-hante-poster.jpg', 1, '29.99', '2023-05-10 18:00:00.000000', NULL, 2),
(42, 'le-secret-du-labyrinthe', 'Le Secret du Labyrinthe', 'Un groupe d\'amis découvre un labyrinthe caché dans les bois, mais ils ne sont pas seuls...', 'le-secret-du-labyrinthe-poster.jpg', 1, '19.99', '2023-05-12 20:30:00.000000', NULL, 3),
(43, 'les-montagnes-du-nord', 'Les montagnes du Nord', 'Une histoire d\'amour dans les montagnes du nord de la France', 'poster-les-montagnes-du-nord.jpg', 1, '25.00', '2023-05-09 10:30:00.000000', '2023-05-09 10:30:00.000000', 3),
