(45, 'les-croisieres-sous-marines', 'Les croisières sous-marines', 'Un voyage inoubliable pour découvrir les merveilles de la mer', 'poster-les-croisieres-sous-marines.jpg', 1, '50.00', '2023-05-11 19:30:00.000000', '2023-05-11 19:30:00.000000', 1),
(46, 'le-chateau-de-la-lune', 'Le château de la lune', 'Une pièce de théâtre captivante sur la vie d\'une famille royale', 'https://example.com/posters/le-chateau-de-la-lune.jpg', 1, '20.00', '2022-02-12 10:30:00.000000', NULL, 5),
(47, 'la-magie-de-l-hiver', 'La magie de l\'hiver', 'Un spectacle de patinage sur glace avec des acrobaties incroyables', 'https://example.com/posters/la-magie-de-l-hiver.jpg', 1, '30.00', '2022-02-18 19:00:00.000000', NULL, 8),
